import {BaseBehaviour} from "../components/generalize/BaseBehaviour";
import {Method} from "../components/generalize/Method";

/**
 * A library for building mocypress-ui-core dependency lib
 *
 * @remarks
 * The mocypress-ui-core defines the {@link Selectable} class and {@link BaseBehaviour} abstract class,
 * which are used to build element selectable behaviours
 *
 * @packageDocumentation
 */

/**
 * Class implemented by to re-present all generalize selectable behaviours for web-components
 * @public
 */
export class Selectable extends BaseBehaviour{

    constructor(locator: string, method: Method) {
        super(locator,method);
    }

    public select(select: string): void{
        this.element.select(select);
    }

}