import {BaseBehaviour} from "../components/generalize/BaseBehaviour";
import {Method} from "../components/generalize/Method";

/**
 * A library for building mocypress-ui-core dependency lib
 *
 * @remarks
 * The mocypress-ui-core defines the {@link Typeable} class and {@link BaseBehaviour} abstract class,
 * which are used to build element Typeable behaviours
 *
 * @packageDocumentation
 */

/**
 * Class implemented by to re-present all generalize typeable behaviours for web-components
 * @public
 */
export class Typeable extends BaseBehaviour{

    constructor(locator: string, method: Method) {
        super(locator, method);
    }

    public type(text: string) : void{
        this.element.type(text);
    }
}