import {Element} from "../generalize/Element";
import {Method} from "../generalize/Method";
import {Typeable} from "../../behaviours/Typeable";
import {Clickable} from "../../behaviours/Clickable";

/**
 * A library for building mocypress-ui-core dependency lib
 *
 * @remarks
 * The mocypress-ui-core defines the common check-box implementation connect with {@link Element} class,
 * which are used to build generic check-box
 *
 * @packageDocumentation
 */

/**
 * Class common check-box implemented using its behaviours and actions.
 * @public
 */
export class BaseCheckBox extends Element{

    private clickable: Clickable;

    constructor(locator: string, method: Method) {
        super(locator, method);
        this.clickable = new Clickable(locator, method);
    }

    private check(): void{
        this.clickable.click();
    }

    private uncheck(): void{
        this.clickable.click();
    }
}