import {Element} from "../generalize/Element";
import {Method} from "../generalize/Method";
import {Typeable} from "../../behaviours/Typeable";
import {Clickable} from "../../behaviours/Clickable";
import {Selectable} from "../../behaviours/Selectable";

/**
 * A library for building mocypress-ui-core dependency lib
 *
 * @remarks
 * The mocypress-ui-core defines the common drop-down implementation connect with {@link Element} class,
 * which are used to build generic drop-down
 *
 * @packageDocumentation
 */

/**
 * Class common drop-down implemented using its behaviours and actions.
 * @public
 */
export class BaseDropDown extends Element{

    private selectable: Selectable;

    constructor(locator: string, method: Method) {
        super(locator, method);
        this.selectable = new Selectable(locator, method);
    }

    private select(select: string): void{
        this.selectable.select(select);
    }

}