import {Element} from "../generalize/Element";
import {Method} from "../generalize/Method";
import {Typeable} from "../../behaviours/Typeable";

/**
 * A library for building mocypress-ui-core dependency lib
 *
 * @remarks
 * The mocypress-ui-core defines the common text-field implementation connect with {@link Element} class,
 * which are used to build generic text-field
 *
 * @packageDocumentation
 */

/**
 * Class common text-field implemented using its behaviours and actions.
 * @public
 */
export class BaseTextField extends Element{

    private typeable: Typeable;

    constructor(locator: string, method: Method) {
        super(locator, method);
        this.typeable = new Typeable(locator, method);
    }

    private type(text: string): void{
        this.typeable.type(text);
    }
}