import {BaseButton} from "../base-elements/BaseButton";
import {Method} from "../generalize/Method";

/**
 * A library for building mocypress-ui-core dependency lib
 *
 * @remarks
 * The mocypress-ui-core defines the button component connect with {@link BaseButton} and {@link Element} class,
 * which are used to build generic button
 *
 * @packageDocumentation
 */

/**
 * Class expose button component using its behaviours and actions to test level.
 * @public
 */
export class Button extends BaseButton{

    constructor(locator: string, method: Method = Method.XPATH) {
        super(locator, method);
    }
}