import {BaseTextField} from "../base-elements/BaseTextField";
import {Method} from "../generalize/Method";

/**
 * A library for building mocypress-ui-core dependency lib
 *
 * @remarks
 * The mocypress-ui-core defines the text-field component connect with {@link BaseTextField} and {@link Element} class,
 * which are used to build generic text-field
 *
 * @packageDocumentation
 */

/**
 * Class expose text-field component using its behaviours and actions to test level.
 * @public
 */
export class TextField extends BaseTextField{

    constructor(locator: string, method: Method = Method.XPATH) {
        super(locator, method);
    }
}