/// <reference types="cypress-xpath" />
import {Method} from "../generalize/Method";
import {FrameworkException} from "../../cypress-core/exception/FrameworkException";

/**
 * A library for building mocypress-ui-core dependency lib
 *
 * @remarks
 * The mocypress-ui-core defines the {@link BaseBehaviour} abstract class,
 * which are used to build abstract element behaviour common config
 *
 * @packageDocumentation
 */

/**
 * Class implemented by to config all generalize web component access using given locator types
 * @public
 */
export abstract class BaseBehaviour {

    // @ts-ignore
    public element: Cypress.Chainable<JQuery<HTMLElement>>

    protected constructor(locator: string, selectionMethod: Method) {
        switch (selectionMethod) {
            case Method.XPATH:
                this.element = cy.xpath(locator)
                break;
            case Method.CYPRESS_GET:
                this.element = cy.get(locator)
                break;
            default: {
                throw new FrameworkException("Error ! Please choose element correct locator type...")
                break;
            }
        }
    }
}
